class Util:

    def __init__(self, a, b):
        self.a = a
        self.b = b

    @staticmethod
    def hello_world():
        print("hello world ishaan welcome hi")

    def sum(self):
        return self.a + self.b

    def sum_extended(self):
        return self.a * self.a + self.b

    def sum_extended_version2(self):
        return self.a * self.a + self.b * self.b
