ihm:html.script:
    function loadGoogleMap(id,lat,long) {
      var myLatLng = {lat: lat, lng: long};
      var map = new google.maps.Map(document.getElementById(id), {
        center: myLatLng,
        zoom: 6
      });
      var marker = new google.maps.Marker({
        position: myLatLng,
        map: map,
        title: 'Hello World!'
      });
    }

    function loadLeftMap(id,lat,long) {
        var map = L.map(id).setView([51.505, -0.09], 13);
        var marker = L.marker([lat,long]).addTo(map);
        marker.bindPopup("I am a popup.").openPopup();    

        var OpenStreetMap_Mapnik = L.tileLayer('http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
	        maxZoom: 19,
	        attribution: 'OpenStreetMap'
        });
        OpenStreetMap_Mapnik.addTo(map);
    }


    $(document).ready(function() {
        loadMap("ol_map",50.5,10.5)
        loadGoogleMap("google_map",50.5,10.5)
        loadLeftMap("left_map",50.5,10.5)
    })
